import React, {useState} from "react";

function CreateArea(props) {

const [noteContent, setNoteContent] = useState({
  title: "",
  content: ""
})

function updateNote(event) {

const name = event.target.name;
const value = event.target.value
console.log(value)

if (name === "title") {
  setNoteContent((prevValues => {
    return ({
      title: value,
      content: prevValues.content
    })
  }))
} else if (name === "content") {
  setNoteContent((prevValues => {
    return ({
      title: prevValues.title,
      content: value
    })
  }))
}
}

function setNote (event) {
  props.onAdd(noteContent)

setNoteContent({title:"", content:""})
event.preventDefault()
}

  return (
    <div>
      <form>
        <input onChange={updateNote} name="title" placeholder="Title" value={noteContent.title}/>
        <textarea onChange={updateNote} name="content" placeholder="Take a note..." rows="3" value={noteContent.content}/>
        <button  >Add</button> <Button onClick={setNote}>Add</Button>
      </form>
    </div>
  );

}

export default CreateArea;
