    <div>
      <form>
        <input onChange={updateNote} name="title" placeholder="Title" value={noteContent.title}/>
        <textarea onChange={updateNote} name="content" placeholder="Take a note..." rows="3" value={noteContent.content}/>
        <button onClick={setNote} >Add</button>
      </form>
    </div>
  );

}

export default CreateArea;
